import React, {useState, useEffect} from 'react';
import {Link} from "react-router-dom";
import {Button} from "../Button/Button";
import './Navbar.css';
import laptopIcon from '../../images/icons8/icons8-laptop-computer-64.png';
import deleteIcon from '../../images/icons8/icons8-multiply-48.png';
import menuIcon from '../../images/icons8/icons8-menu-24.png';
import {Container, Row, Col} from 'reactstrap';


const Navbar = () => {

    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    }

    useEffect(() => {
        showButton()
    }, [])

    window.addEventListener('resize', showButton);

    return (
        <nav className='navbar'>
            <div className='navbar-container'>
                <Container fluid>
                    <Row>
                        <Col md = "2">
                            <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
                                <Col xl="1">
                                    DIGISHOP
                                </Col>
                                <Col xl="1">
                                    <img src={laptopIcon}/>
                                </Col>
                            </Link>
                        </Col>

                        <Col xs="6" sm="4" lg="2" xl="1">
                            <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                                <li className='nav-item'>
                                    <Col xs="1" sm="1" lg="1" xl="1">
                                        <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                                            Home
                                        </Link>
                                    </Col>
                                </li>
                                <li className='nav-item'>
                                    <Col xs="1" sm="1" lg="1" xl="1">
                                        <Link to='/brands' className='nav-links' onClick={closeMobileMenu}>
                                            Brands
                                        </Link>
                                    </Col>
                                </li>
                                <li className='nav-item'>
                                    <Col xs="1" sm="1" lg="1" xl="1">
                                        <Link to='/profile' className='nav-links' onClick={closeMobileMenu}>
                                            Profile
                                        </Link>
                                    </Col>
                                </li>
                                <li className='nav-item'>
                                    <Col xs="1" sm="1" lg="1" xl="1">
                                        <Link to='/contact-us' className='nav-links' onClick={closeMobileMenu}>
                                            Contact Us
                                        </Link>
                                    </Col>
                                </li>
                                <li className='nav-item'>
                                    <Col xs="1" sm="1" lg="1" xl="1">
                                        <Link to='/contact-us' className='nav-links' onClick={closeMobileMenu}>
                                            Categories
                                        </Link>
                                    </Col>
                                </li>

                                <Col xs="1" sm="1" lg="1" xl="1">
                                    {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}
                                </Col>
                            </ul>
                        </Col>
                        <Col xs="1" sm="1" lg="1" xl="1">
                            <div className='menu-icon' onClick={handleClick}>
                                <img src={click ? {deleteIcon} : {menuIcon}}/>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </div>
        </nav>
    )
}

export default Navbar
